def split_file(file_path, lines_per_file):
    with open(file_path, 'r') as file:
        lines = file.readlines()
        for i in range(0, len(lines), lines_per_file):
            file_part = lines[i:i + lines_per_file]
            with open(f'alternateNames_split_{i // lines_per_file}.txt', 'w') as new_file:
                new_file.writelines(file_part)

split_file('alternateNames.txt', 10000)